import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import Modal from 'react-modal';

const ChatBubble = ({ url }) => {
  const location = useLocation();
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    // Restablecer el estado del modal cuando cambia la ruta
    setModalIsOpen(false);

    if (location.pathname !== '/login') {
      // Coloca tu lógica para inyectar scripts aquí
      const injectScript = document.createElement('script');
      injectScript.src = 'https://cdn.botpress.cloud/webchat/v1/inject.js';
      injectScript.async = true;
      document.body.appendChild(injectScript);

      const configScript = document.createElement('script');
      configScript.src = 'https://mediafiles.botpress.cloud/634d2b82-4a73-4676-85d7-764180e85b0f/webchat/config.js';
      configScript.defer = true;
      document.body.appendChild(configScript);

      return () => {
        document.body.removeChild(injectScript);
        document.body.removeChild(configScript);
      };
    }
  }, [location.pathname]);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  if (location.pathname === '/login') {
    return null; // Si estás en la ruta "/login", no mostrar la burbuja
  }

  return (
    <div style={{ position: 'fixed', bottom: '10px', right: '10px' }}>
      <div className="chat-bubble" onClick={openModal}>
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Chat Modal"
        style={{
          overlay: {
            backgroundColor: 'transparent',
          },
          content: {
            position: 'absolute',
            bottom: '0',
            right: '0',
            left: 'auto',
            top: 'auto',
            border: '1px solid #ccc',
            background: '#fff',
            overflow: 'hidden',
            borderRadius: '8px',
            outline: 'none',
            padding: '20px',
            maxWidth: '400px',
            maxHeight: '80vh',
          },
        }}
      >
        <button onClick={closeModal} style={{ background: 'none', border: 'none', cursor: 'pointer' }}>Cerrar</button>
        <iframe title="Chat Bot" src={url} width="100%" height="400px"></iframe>
      </Modal>
    </div>
  );
};

export default ChatBubble;
