      codigoOrientadorGasto,
      descripcionGasto,
      asignacionInicial,
      codificado,
      reformaPresupuesto,
      presupuestoCodificado2023,
      traspasos,
      presupuestoCodificado,
      id,
    } = cliente;

    const agenteUid = user.id;
    console.log(cliente);
    try {
      const { data, error } = await supabase
        .from('pacPlanificacion')
        .insert({
