import React, { useState } from 'react';
import Modal from 'react-modal';

const ChatBubble = ({ url }) => {
  const [modalIsOpen, setModalIsOpen] = useState(false);

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <div style={{ position: 'fixed', bottom: '10px', right: '10px' }}>
      <div className="chat-bubble" onClick={openModal}>
        <img src="https://cdn-icons-png.freepik.com/512/10307/10307948.png" alt="Chat Icon" style={{ width: '60px', height: '60px' }} />
      </div>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Chat Modal"
        style={{
          overlay: {
            backgroundColor: 'transparent', // esto quita el fondo gris y lo hace transparente 
          },
          content: {
            position: 'absolute',
            bottom: '0',
            right: '0',
            left: 'auto',
            top: 'auto',
            border: '1px solid #ccc',
            background: '#fff',
            overflow: 'hidden',
            borderRadius: '8px',
            outline: 'none',
            padding: '20px',
            maxWidth: '400px',
            maxHeight: '80vh',
          },
        }}
      >
        <button onClick={closeModal}><img src="https://cdn-icons-png.flaticon.com/512/463/463612.png" alt="Chat Icon" style={{ width: '30px', height: '30px' }} /></button>
        <iframe title="Chat Bot" src={url} width="100%" height="400px"></iframe>
      </Modal>
    </div>
  );
};

export default ChatBubble;
